class Line(object):
    
    def __init__(self, position, width, color, orig_x, orig_y, orig_width, orig_height):
        self.position = position
        self.width = width
        self.color = color
        self.orig_x = orig_x
        self.orig_y = orig_y
        self.orig_width = orig_width
        self.orig_height = orig_height
